import React from "react";
import style from './Form.module.css'
import { useState } from "react";
import validate from "./validation";

const Formulario = () => {
    const [form, setForm] = useState({
      username: "",
      password: "",
    });
  
    const [errors, setErrors] = useState({
      // un estado para los errores
      username: "",
      password: "",
    });
  
    const handleChange = (event) => {
      const property = event.target.name; // dejo en claro cual es la propiedad que quiero modificar
      const value = event.target.value; // dejo en claro el valor que quiero darle
  
      setForm({ ...form, [property]: value }); // aqui a traves del setForm agregamos a lo que tenia form el valor a la propiedad
      validate({ ...form, [property]: value }, setErrors, errors); // validamos si el input del mail esta o no vacio
    };
  
    const submithandler = (event) => {
      event.preventDefault(); // el preventDefault solo es para el event submit
      alert("login existoso");
    };
    
    return (
      <div className={style.divForm}>
      <form onSubmit={submithandler}>
        <div>
          <label htmlFor="username" >username</label>
          <input
            type="text"
            name="email"
            value={form.email}
            className={errors.email ? style.error : style.success}
            onChange={handleChange}
            />
          <span>{errors.email}</span> 
        </div>
        <div>
          <label htmlFor="password">Paassword</label>
          <input
            type="text"
            name="password"
            value={form.password}
            onChange={handleChange}
            />
        </div>
        <button type="submit">Login</button>
      </form>
      </div>
    );
  };
  
  export default Formulario;
