import React, { useState } from "react";
import style from "./card.module.css";
import { Link } from "react-router-dom";
import { agregarPersonaje, removePersonaje } from "../../redux/actions";
import { connect } from "react-redux";

function Card(props) {
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav === true) {
      setIsFav(false);
      removePersonaje(props.id)
    } else {
      setIsFav(true);
      agregarPersonaje(props.id);
    }
  };

  return (
    <div className={style.divGeneral}>+
      {isFav ? (
        <button onClick={handleFavorite}>‚ù§Ô∏è</button>
      ) : (
        <button onClick={handleFavorite}>ü§ç</button>
      )}
      <button className={style.boton} onClick={props.onClose}>
        X
      </button>
      <img className={style.img} src={props.image} alt="" />
      <div className={style.nameContainer}>
        <Link to={`/detail/${props.id}`}>
          <h2 className={style.name}>{props.name}</h2>
        </Link>
      </div>
      <h2 className={style.species}>{props.species}</h2>
      <h2 className={style.gender}>{props.gender}</h2>
    </div>
  );
}

const mapDispachToPops = (dispatch) => {
  return {
    agregarPersonaje: (id) => {
      dispatch(agregarPersonaje(id));
    },
    removePersonaje: (id) => {
      dispatch(removePersonaje(id));
    },
  };
};

export default connect(null, mapDispachToPops)(Card);
