const validate = (form, setErrors, errors) => {
    // recibe l form y se vija si el nomnbre esta bien si no es asi que guarde en un estado el error
    // validando el mail
    if (!form.email)
      setErrors({...errors, email: "email vacio",}); // aplicamos el set errors y lo pasmos por parametro por que esta afuera de la funcion general
    else {if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email))
    setErrors({ ...errors, email: "" });
      else setErrors({ ...errors, email: "email invalido" });
    }
    
  };

  export default validate;
