import React from "react";
import style from './Form.module.css'
import { useState } from "react";

const validate = (form, setErrors, errors) => {
    // recibe l form y se vija si el nomnbre esta bien si no es asi que guarde en un estado el error
    // validando el mail
    if (!form.email)
      setErrors({...errors, email: "email vacio",}); // aplicamos el set errors y lo pasmos por parametro por que esta afuera de la funcion general
    else {if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email))
    setErrors({ ...errors, email: "" });
      else setErrors({ ...errors, email: "email invalido" });
    }
    
  };
  
const Formulario = () => {
    const [form, setForm] = useState({
      email: "",
      password: "",
    });
  
    const [errors, setErrors] = useState({
      // un estado para los errores
      email: "",
      password: "",
    });
  
    const handleChange = (event) => {
      const property = event.target.name; // dejo en claro cual es la propiedad que quiero modificar
      const value = event.target.value; // dejo en claro el valor que quiero darle
  
      setForm({ ...form, [property]: value }); // aqui a traves del setForm agregamos a lo que tenia form el valor a la propiedad
      validate({ ...form, [property]: value }, setErrors, errors); // validamos si el input del mail esta o no vacio
    };
  
    const submithandler = (event) => {
      event.preventDefault(); // el preventDefault solo es para el event submit
      alert("login existoso");
    };
    
    return (
      <div className={style.divG}>
      <form onSubmit={submithandler}>
        <div>
          <label htmlFor="username">email</label>
          <input
            type="text"
            name="email"
            value={form.email}
            onChange={handleChange}
            className={errors.email ? style.error : style.success}
            />
          <span>{errors.email}</span> {/* nos muesta el mensaje del estado de lo que vamos escribiendo */}
        </div>
        <div>
          <label htmlFor="password">Paassword</label>
          <input
            type="text"
            name="password"
            value={form.password}
            onChange={handleChange}
            />
        </div>
        <button type="submit">Login</button>
      </form>
      </div>
    );
  };
  
  export default Formulario;
