import { ADD_PERSONAJE, REMOVE_PERSONAJE } from "./actions-types"

export const agregarPersonaje = (fav) => {
    return {type: ADD_PERSONAJE, payload: fav}
}

export const removePersonaje = (id) => {
    return {type: REMOVE_PERSONAJE, payload: id}
}
