import { ADD_PERSONAJE, REMOVE_PERSONAJE } from "./actions-types";

const initialState = {
  myFavorites: [],
};

const rootReducer = (state = initialState, action) => {
  //recibe el estado que modifica y la accion
  switch (action.type) {
    case ADD_PERSONAJE: // ejemplo
      return { ...state, myFavorites: [...state.my, action.payload] }; /// este caso para agregar un personaje

    case REMOVE_PERSONAJE : // ejemplo
      return {
        ...state,
        myFavorites: state.myFavorites.filter((char) => char.id !== action.payload),
      }; // para quitar un personaje

    default:
      return { ...state }; //devuelve una copia del estado
  }
};

export default rootReducer;
