const validate = (form, setErrors, errors) => {
  // recibe l form y se vija si el nomnbre esta bien si no es asi que guarde en un estado el error
  // validando el mail
  if (!form.username)
    setErrors({
      ...errors,
      username: "nombre de usuario vacio",
    }); // aplicamos el set errors y lo pasmos por parametro por que esta afuera de la funcion general
  else {
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.username))
      setErrors({ ...errors, username: "" });
    else setErrors({ ...errors, username: "usuario invalido" });
  }

  if (!form.username) {
    setErrors({...errors, username: "nombre de usuario vacio"});
  } else if (form.username.length > 35) {
    setErrors({...errors, username: "el nombre de usuario no puede tener más de 35 caracteres"});
  } else {
    setErrors({...errors, username: ""});
  }
};
export default validate;
