import Card from "./Card";
import style from './cards.module.css'

export default function Cards(props) {
  const { characters } = props;
  const [activeCharacters, setActiveCharacters] = useState(characters);

  const handleCloseCard = (name) => {
    setActiveCharacters((prevCharacters) =>
      prevCharacters.filter((personaje) => personaje.name !== name)
    );
  };
  return (
    <div className = {style.container}>
      {props.characters.map((personaje) => {
        return (
          <Card  
            name={personaje.name}
            species={personaje.species}
            gender={personaje.gender}
            image={personaje.image}
            onClose={() => window.alert("Emulamos que se cierra la card")}
          />
        );
      })}
    </div>
  );
}
// en este componente agregamos card y pasamos por props
