import "./App.css";
import Cards from "./components/Cards/Cards";
import Nav from "./components/Nav/Nav";
import { StrictMode, useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import About from "./view/About/About";
import Detail from "./view/Detail/Detail";
import NotFound from "./components/NotFound/NotFound";
import Formulario from "./components/Form/Form";
import { useLocation, useNavigate } from "react-router-dom";
import audioFile from "./public/Hombres Lunares Versión Oficial (Completo) feat. Pedo y Morty  Rick  Morty  Adult Swim.mp3";
import { FaVolumeMute, FaVolumeUp } from "react-icons/fa";


function App() {
  const [characters, setCharacters] = useState([]);
  const [muted, setMuted] = useState(true);

  const toggleMute = () => {
    setMuted((prevState) => !prevState);
  };
  const audioProps = {
    src: audioFile,
    autoPlay: true,
    loop: true,
    muted,
  };

  const onSearch = (character) => {
    fetch(`https://rickandmortyapi.com/api/character/${character}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.name) {
          const idExists = characters.some((char) => char.id === data.id); // utilizamos some
          if (idExists) {
            window.alert("El ID ya existe en la lista de personajes");
          } else {
            setCharacters((oldChars) => [...oldChars, data]);
          }
        } else {
          window.alert("No hay personajes con ese ID");
        }
      });
  };

  const onClose = (id) => {
    const filtrar = characters.filter(
      (character) => character.id !== Number(id)
    );
    setCharacters(filtrar);
  };
  const onRandomCharacter = () => {
    const randomId = Math.floor(Math.random() * 826) + 1; // generar un id aleatorio entre 1 y 108
    fetch(`https://rickandmortyapi.com/api/character/${randomId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.name) {
          const idExists = characters.some((char) => char.id === data.id);
          if (idExists) {
            window.alert("El ID ya existe en la lista de personajes");
          } else {
            setCharacters((oldChars) => [...oldChars, data]);
          }
        } else {
          window.alert("No se pudo obtener un personaje aleatorio");
        }
      });
  };
  const location = useLocation();

  // Verifica si la ruta actual es "/" y oculta el componente Nav
  const hideNav = location.pathname === "/";

  const navigate = useNavigate();
  const [access, setAccess] = useState(false);
  const username = "cristian84molina@gmail.com";
  const password = "1password";

  function login(userData) {
    if (userData.password === password && userData.email === username) {
      setAccess(true);
      navigate("/home");
    }
  }

  useEffect(() => {
    !access && navigate("/");
  }, [access]);

  return (
    <StrictMode>
      <div className="App" style={{ padding: "40px" }}>
        {!hideNav && (
          <Nav onSearch={onSearch} onRandomCharacter={onRandomCharacter} />
        )}

        <audio {...audioProps} />
        
        <button onClick={toggleMute}>
          {muted ? <FaVolumeMute /> : <FaVolumeUp />}
        </button>
        


        <Routes>
          <Route path="/" element={<Formulario login={login} />} />

          <Route path="/about" element={<About />} />

          <Route
            path="/home"
            element={<Cards characters={characters} onClose={onClose} />}
          />

          <Route path="/detail/:detailId" element={<Detail />} />

          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    
    </StrictMode>
  );
}

export default App;
