import React, { useState } from "react";
import style from './searchBar.module.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';



export default function SearchBar(props) {

  const [character, setCharacter] = useState ('') // es lo que escribe el usuario
  const [searchResults, setSearchResults] = useState([]);

  const handleChange = (event) => {
    setCharacter(event.target.value);

    // Aquí podrías hacer la búsqueda de personajes y actualizar el estado de "searchResults"
    const results = props.characters.filter((character) =>
    character.name.toLowerCase().includes(event.target.value.toLowerCase())
  );
  setSearchResults(results);
};

const handleSelectCharacter = (selectedCharacter) => {
  // Aquí podrías hacer algo cuando el usuario selecciona un personaje de la lista de resultados
  setCharacter(selectedCharacter.name);
  setSearchResults([]);
};


  return (
    <div className={style.container}>
      <h1 className={style.title}>Rick And Morty</h1>
      <input className={style.input} type="search" placeholder="Ingrese el ID de personaje" onChange={handleChange}/>
      <button className={style.boton} onClick={()=>props.onSearch(character)}><FontAwesomeIcon icon={faSearch} /></button>
      <button className={style.boton1} onClick={()=>props.onRandomCharacter(character)}>Personaje Random  <FontAwesomeIcon icon={faSearch} /></button>
    {/* Aquí se muestra la lista de resultados */}
    {searchResults.length > 0 && (
        <ul className={style.results}>
          {searchResults.map((result) => (
            <li key={result.id} onClick={() => handleSelectCharacter(result)}>
              {result.name}
            </li>
          ))}
        </ul>
      )}
    
    </div>
  );
}

